import React, { useEffect, useState } from "react";



const TeamRecordings = () => {
  const [selectedFile, setSelectedFile] = useState(null);

  const files = [
    { name: 'Milliman Introduction ', type: 'word', path: 'https://hexawareonline.sharepoint.com/teams/ACFCAmeriHealthInduction/_layouts/15/stream.aspx?id=%2Fteams%2FACFCAmeriHealthInduction%2FShared%20Documents%2FGeneral%2F04%20Projects%20and%20Tracks%20in%20ACFC%2FMilliman%2FMiliman%20Introduction%2D20240215%5F120454%2DMeeting%20Recording%2Emp4&referrer=StreamWebApp%2EWeb&referrerScenario=AddressBarCopied%2Eview' },
    { name: 'Fee Schedule', type: 'word', path: 'https://hexawareonline.sharepoint.com/teams/ACFCAmeriHealthInduction/_layouts/15/stream.aspx?id=%2Fteams%2FACFCAmeriHealthInduction%2FShared%20Documents%2FGeneral%2F04%20Projects%20and%20Tracks%20in%20ACFC%2FFee%20Schedule%2FFee%20Schedule%20introduction-20240223_120240-Meeting%20Recording%2Emp4&referrer=SharePoint&referrerScenario=OpenFile' }
  ];

  const handleFileDisplay = (path) => {
    // window.open(path, '_blank');
    window.location.href = path;
  };
  

  return (
    <div className="container2">
      <div className="buttons-container">
      {files.map((file, index) => (
        // <button key={index} className="button" onClick={() => handleFileDisplay(file.path)}>
        //   {file.name}
        // </button>
        <button key={index} className="button2" onClick={() => handleFileDisplay(file.path)}>
  {file.name}
</button>

      ))}
      {selectedFile && (
        <div className="file-container">
          {/* Display the selected file based on its type */}
          {selectedFile.endsWith('.png') && <img className="file-display" src={selectedFile} alt="File" />}
          {selectedFile.endsWith('.pptx') && <iframe className="file-display" src={selectedFile} title="File" />}
          {selectedFile.endsWith('.pdf') && <iframe className="file-display" src={selectedFile} title="File" />}
          {selectedFile.endsWith('.docx') && <iframe className="file-display" src={selectedFile} title="File" />}
          {selectedFile.endsWith('.xlsx') && <iframe className="file-display" src={selectedFile} title="File" />}
          {selectedFile.endsWith('.video') && (
            <video className="file-display" src={selectedFile} controls>
              Your browser does not support the video tag.
            </video>
          )}
        </div>
      )}
      </div>
    </div>
  );
};



export default TeamRecordings;
