import React, { useState } from "react";
import "./Hedis.css";
import { RiUpload2Line } from 'react-icons/ri';

const Hedis = () => {
  const [selectedFile, setSelectedFile] = useState(null);

  const files = [
    { name: 'Hedis_2.0_Project Overview_PPT', type: 'ppt', path: 'https://hexawareonline.sharepoint.com/:p:/r/teams/ACFCAmeriHealthInduction/Shared%20Documents/General/01%20ACFC%20Overview/HEDIS/HEDIS_2_0_Project_Overview.pptx?d=w9babe53b4d6f489f81f97d912638820e&csf=1&web=1&e=rhk5Rg' },
  ];

  const handleFileDisplay = (path) => {
    window.location.href = path;
  };

  return (
    <div className="container1">
      
      {files.map((file, index) => (
        <button key={index} className="button1" onClick={() => handleFileDisplay(file.path)}>
          {file.name}
        </button>
      ))}
      {selectedFile && (
        <div className="file-container">
          {/* Display the selected file based on its type */}
          {/* {selectedFile.endsWith('.png') && <img className="file-display" src={selectedFile} alt="File" />} */}
          {selectedFile.endsWith('.pptx') && <iframe className="file-display" src={selectedFile} title="File" />}
        </div>
      )}
    </div>
  );
};

export default Hedis;
