import React, { useState } from "react";



const Digital = () => {
  const [selectedFile, setSelectedFile] = useState(null);

  const files = [
    { name: 'Data Analytics Intro Meeting Recording', type: 'video', path: 'https://hexawareonline.sharepoint.com/teams/ACFCAmeriHealthInduction/_layouts/15/stream.aspx?id=%2Fteams%2FACFCAmeriHealthInduction%2FShared%20Documents%2FGeneral%2F04%20Projects%20and%20Tracks%20in%20ACFC%2FDigital%2FData%20Analytics%20Introduction%2D20240216%5F121221%2DMeeting%20Recording%2Emp4&referrer=StreamWebApp%2EWeb&referrerScenario=AddressBarCopied%2Eview' },
    { name: 'Hedis Intro Meeting Recording', type: 'word', path: 'https://hexawareonline.sharepoint.com/teams/ACFCAmeriHealthInduction/_layouts/15/stream.aspx?id=%2Fteams%2FACFCAmeriHealthInduction%2FShared%20Documents%2FGeneral%2F04%20Projects%20and%20Tracks%20in%20ACFC%2FHEDIS%2FHEDIS%20Introduction%2D20240219%5F113640%2DMeeting%20Recording%20%281%29%2Emp4&referrer=StreamWebApp%2EWeb&referrerScenario=AddressBarCopied%2Eview' }
  ];

  const handleFileDisplay = (path) => {
    // window.open(path, '_blank');
    window.location.href = path;
  };
  

  return (
    <div className="container2">
      <div className="buttons-container">
      {files.map((file, index) => (
        // <button key={index} className="button" onClick={() => handleFileDisplay(file.path)}>
        //   {file.name}
        // </button>
        <button key={index} className="button2" onClick={() => handleFileDisplay(file.path)}>
  {file.name}
</button>
        

      ))}
      {selectedFile && (
        <div className="file-container">
          {/* Display the selected file based on its type */}
          {selectedFile.endsWith('.png') && <img className="file-display" src={selectedFile} alt="File" />}
          {selectedFile.endsWith('.pptx') && <iframe className="file-display" src={selectedFile} title="File" />}
          {selectedFile.endsWith('.pdf') && <iframe className="file-display" src={selectedFile} title="File" />}
          {selectedFile.endsWith('.docx') && <iframe className="file-display" src={selectedFile} title="File" />}
          {selectedFile.endsWith('.xlsx') && <iframe className="file-display" src={selectedFile} title="File" />}
          {selectedFile.endsWith('.video') && (
            <video className="file-display" src={selectedFile} controls>
              Your browser does not support the video tag.
            </video>
          )}
        </div>
      )}
      </div>
    </div>
  );
};
export default Digital;
